import styles from './styles.module.css';
import { useNavigate } from 'react-router-dom';

export default function Home(props) {
	const {username, setUsername, room, setRoom, socket} = props;
	const navigate = useNavigate();
	
	const joinRoom = () => {
		if (room !== '' && username !== '') {
			socket.emit('join_room', { username, room });
			navigate('/chat', { replace: true });
		}
	}

	return (
		<div className={styles.container} >
			<div className={styles.formContainer}>
				<h1>{`Queue System Prototype`}</h1>
				<input onChange={(e) => setUsername(e.target.value)} className={styles.input} placeholder="Username..."></input>

				<button onClick={joinRoom} className="btn btn-secondary" style={{ width: '100%' }}>Join as a Patient</button>
				<button onClick={joinRoom} className="btn btn-secondary" style={{ width: '100%' }}>Join as a Doctor</button>
			</div>
		</div>
	)
}
