import styles from './styles.module.css';
import { useNavigate } from 'react-router-dom';

export default function Home(props) {
	const {username, setUsername, role, setRole, socket} = props;
	const navigate = useNavigate();
	
	const joinQueue = () => {
		if (role !== '' && username !== '') {
			socket.emit('join_queue', { username, role });
			navigate(`/${role}`, { replace: true });
		}
	}

	const handleRoleChange = (e) => {
    setSelectedOption(e.target.value);
  };

	return (
		<div className={styles.container} >
			<div className={styles.formContainer}>
				<h1>{`Queue System Prototype`}</h1>
				<input onChange={(e) => setUsername(e.target.value)} className={styles.input} placeholder="Username..."></input>

				<select onChange={handleRoleChange} className={styles.input}>
					<option>--- Select Role ---</option>
					<option value="patient">Patient</option>
					<option value="doctor">Doctor</option>
				</select>

				<button onClick={joinQueue} className="btn btn-secondary" style={{ width: '100%' }}>Join</button>
			</div>
		</div>
	)
}
