// client/src/pages/chat/index.js

import styles from './styles.module.css';
import { useState, useEffect } from 'react';
import DoctorList from './doctor-list';

const Patient = ({ user, setUser, socket }) => {

  const [timeLeft, setTimeLeft] = useState(0);
  const [estimateTime, setEstimateTime] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      setTimeLeft(calculateTimeLeft());
    }, 1000);

    return () => clearTimeout(timer);
  });

  const addLeadingZeros = (value) => {
    return value < 10 ? `0${value}` : value;
  };

  // Runs whenever a socket event is recieved from the server
  useEffect(() => {
    socket.on('queue_update', (data) => {   
      const {userList} = data
      userList.forEach(item => {
        if (user.username === item.username) {
          setUser(item)
        }
      });      
    });

	// Remove event listener on component unmount
    return () => socket.off('queue_update');
  }, [socket]);

  return (
    <div className={styles.chatContainer}>
      {/* Add this */}
      <DoctorList socket={socket}></DoctorList>
      <div>
        <h1>{user.username} - {user.queue_number} - {user.estimated_time}</h1>
      </div>
      <div>
      <h1>Countdown</h1>
      {timeLeft.days > 0 && <div>{`${timeLeft.days} days`}</div>}
      <div>{`${addLeadingZeros(timeLeft.hours)}:${addLeadingZeros(
        timeLeft.minutes
      )}:${addLeadingZeros(timeLeft.seconds)}`}</div>
    </div>
    </div>
  );
};

export default Patient;
